import React, { useState, useRef, useEffect } from 'react';
import Select from 'react-select'
import './index.css';
import greendb from './greendb.json';
import wordlist from './wordlist.json';
import classes from './Site.module.css';
import { Badge, Chip } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import StackGrid from "react-stack-grid";
import { Web, Videocam, Description, PictureAsPdf} from '@material-ui/icons';


const App = props => {

  const handleClick = (e) => {        
    setSelectedOption({value:e, label:e})            
  }
 
  const top5buttons = () => {
    return (      
      <div class={chipclasses.root}>
      <Chip  label={'Submit the text'} onClick={() => handleClick('text')} />
      </div>
    )
  
  }    
  
  const dataPresent = (input) => {
    return (
      
        <StackGrid
        columnWidth={230}
        gutterWidth={10}        
        gridRef={grid=> grid = grid}
        
      >
      {result.slice(0,150).map((e,idx)=> {return <a href={e.Link} target="_blank"><button key={idx} className={e.Type==="Website"? classes.buttonW : e.Type==='Video'?  classes.buttonV : e.Type==='Article'?  classes.buttonA : classes.buttonR}>{e.Name.slice(0,55)}<div style={{position:"fixed",right:5,bottom:5}}>{e.Type==="Website"? <Web/> : e.Type==='Video'? <Videocam/>  : e.Type==='Article'?  <Description/> : <PictureAsPdf/>}</div> </button><br/></a>})}
      </StackGrid>
      
    )
  
  }    

{/* <button className={classes.buttonActive}>{e.Name}</button> */}

  const [selectedOption, setSelectedOption] = useState({value: '', label: 'Search'});
  console.log(selectedOption)
  // +"  "+e.freq
const options = []
const wordcount = []
const wordAvoid = ["THE","OF","IN","AND","TO","FOR","A","IS","WITH","ON","FROM","-","USING","HOW","BY","NEW","INTO","AN","&","WHAT","ABOUT",2020,"ARE","AT","IT","CHANGE","FIRST",10,"WORLD?????S","USE","OVER","HAS","|","ONE",2019,"TIME",2050,"OR","?????",":"]
// wordlist.map(e => options.push({value: e.name, label: e.name}))

greendb.map(e => {
  e.keyword=e.Name.toUpperCase().split(' ')
  // e.keyword.map(x => wordcount.push(x))    
})

// greendb.map(e => console.log(e.keyword))

const [web, setweb] = useState(true);
const [video, setvideo] = useState(true);
const [report, setreport] = useState(true);
const [article, setarticle] = useState(true);

let typelist = []
if(web){typelist.push("Website")}
if(video){typelist.push("Video")}
if(report){typelist.push("Report")}
if(article){typelist.push("Article")}
console.log(typelist)

let result = selectedOption.value==''? greendb.filter(e => (typelist.includes(e.Type))): greendb.filter(e => (typelist.includes(e.Type) && e.keyword.includes(selectedOption.value)))
result.map(e => e.keyword.map(x => wordcount.push(x)))
const wordresult = wordcount.filter(e => !wordAvoid.includes(e))
// console.log(wordcount)


const counts = {};

wordresult.map(num => counts[num] = counts[num] ? counts[num] + 1 : 1)


// console.log(counts)


// console.log(selectedOption)
const keysSorted = Object.keys(counts).sort(function(b,a){return counts[a]-counts[b]})
console.log(keysSorted);     // bar,me,you,foo

const freqSorted = Object.keys(counts).sort(function(b,a){return counts[a]-counts[b]}).map(key => counts[key]);
console.log(freqSorted);

keysSorted.map((e,idx) => options.push({value:e,label:e+" "+freqSorted[idx]}) )


  
return (
<div class={classes.main}>
  <div class={classes.header}>
    <div class={classes.title}>{"IEA NLP TEST"}</div>
    <div class={classes.maintext}>7613 tweets were used for training the model. BERT model in tensorflow was used for training.<br/>Please type any text and then it will show how close it is that describing real disaster. And click the submit the text button.
    </div>    
  </div>
    <div class={classes.box}>
     <div class={classes.resulttext}>
     <textarea
          type="text" 
          value={selectedOption.value}
          style={{width:"100%",height:300,background:`rgba(${43}, ${43}, ${43}, ${.8})`,color:'white',fontSize:32,float:'left'}}
          onChange={(e) => handleClick(e.target.value)}
        />
      </div>
      <div class={classes.resulttext}>
      <br/><br/>        
      The probablity that the text is about real disaster
      Result: 55%
      {top5buttons()}</div>
    </div>    
    
</div>
  );
};

export default App;
